from freebible import read_web
import random
def getRandVerse():
    web = read_web()
    list =  ['Pslm', 'Matt', 'John', 'Rom', 'Prov', 'Gen', 'Luk', '1 Cor', 'Isa', 'Acts']
    f = 'Gen' 
    b = list[random.randint(0,9)]
    x = random.randint(1,len(web[b]))
    y = random.randint(1,len(web[b][x]))
    return str((web[b][x][y]))
